     * @param  \Illuminate\Http\Request  $request
     * @param  \Closure  $next
     * @return mixed
     */
    public function handle(Request $request, Closure $next)
    {
        $startTime = microtime(true);

        // Process the request
        $response = $next($request);

        $endTime = microtime(true);
        $duration = round(($endTime - $startTime) * 1000, 2); // in milliseconds

        // Log the API request
        $this->logApiRequest($request, $response, $duration);

        return $response;
    }

    /**
     * Log API request details
     *
     * @param  \Illuminate\Http\Request  $request
     * @param  mixed  $response
     * @param  float  $duration
     * @return void
     */
    protected function logApiRequest(Request $request, $response, float $duration): void
    {
        try {
            $logData = [
                'method' => $request->method(),
                'url' => $request->fullUrl(),
                'path' => $request->path(),
                'ip_address' => $request->ip(),
                'user_agent' => $request->userAgent(),
                'request_headers' => json_encode($this->sanitizeHeaders($request->headers->all())),
                'request_body' => json_encode($this->sanitizeRequestBody($request->all())),
                'response_status' => $response->status(),
                'response_body' => $this->getResponseBody($response),
                'duration_ms' => $duration,
                'created_at' => now(),
                'updated_at' => now()
            ];

            // Try to save to database
            try {
                DB::table('api_logs')->insert($logData);
            } catch (\Exception $e) {
                // If database insert fails, log to file instead
                Log::channel('api')->info('API Request', $logData);
            }

        } catch (\Exception $e) {
            Log::error('Failed to log API request', [
                'error' => $e->getMessage(),
                'url' => $request->fullUrl()
            ]);
        }
    }

    /**
