     */
    protected function getResponseBody($response): ?string
    {
        try {
            $content = $response->getContent();
            
            // Limit response body size to prevent large logs
            if (strlen($content) > 10000) {
                return substr($content, 0, 10000) . '... [TRUNCATED]';
            }

            return $content;
        } catch (\Exception $e) {
            return null;
        }
    }
